contracts/FxPositionToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/// @notice Non-transferable ERC20 representing leveraged FX/commodity exposure per market and side
contract FxPositionToken is ERC20, Ownable {
    address public vault;

    event VaultUpdated(address indexed oldVault, address indexed newVault);

    constructor(
        string memory name_,
        string memory symbol_,
        address initialVault
    ) ERC20(name_, symbol_) Ownable(msg.sender) {
        require(initialVault != address(0), "Invalid vault address");
        vault = initialVault;
    }

    modifier onlyVault() {
        require(msg.sender == vault, "Caller is not the vault");
        _;
    }

    function setVault(address newVault) external onlyOwner {
        require(newVault != address(0), "Invalid vault address");
        address oldVault = vault;
        vault = newVault;
        emit VaultUpdated(oldVault, newVault);
    }

    function mint(address to, uint256 amount) external onlyVault {
        _mint(to, amount);
    }

    function burn(address from, uint256 amount) external onlyVault {
        _burn(from, amount);
    }

    /// @dev Override ERC20 transfer to make the token non-transferable for v1
    function _update(address from, address to, uint256 value) internal override {
        if (from != address(0) && to != address(0)) {
            // Disallow transfers between arbitrary users; only mint (from == 0) and burn (to == 0)
            revert("Non-transferable token");
        }
        super._update(from, to, value);
    }
}

contracts/FxPriceOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

contract FxPriceOracle is Ownable {
    uint8 public constant PRICE_DECIMALS = 18;

    // Market IDs are bytes32 (e.g. keccak256("EUR/USD"))
    mapping(bytes32 => uint256) private prices;
    mapping(bytes32 => uint256) private lastUpdated;

    event PriceUpdated(bytes32 indexed marketId, uint256 price, uint256 timestamp);
    event PricesBatchUpdated(bytes32[] marketIds, uint256[] prices, uint256 timestamp);

    constructor() Ownable(msg.sender) {}

    function updatePrice(bytes32 marketId, uint256 price) external onlyOwner {
        require(marketId != bytes32(0), "Invalid marketId");
        require(price > 0, "Price must be greater than zero");

        prices[marketId] = price;
        lastUpdated[marketId] = block.timestamp;

        emit PriceUpdated(marketId, price, block.timestamp);
    }

    function updatePrices(bytes32[] calldata marketIds, uint256[] calldata newPrices) external onlyOwner {
        require(marketIds.length == newPrices.length, "Arrays length mismatch");
        require(marketIds.length > 0, "Empty arrays");

        uint256 timestamp = block.timestamp;

        for (uint256 i = 0; i < marketIds.length; i++) {
            require(marketIds[i] != bytes32(0), "Invalid marketId");
            require(newPrices[i] > 0, "Price must be greater than zero");

            prices[marketIds[i]] = newPrices[i];
            lastUpdated[marketIds[i]] = timestamp;
        }

        emit PricesBatchUpdated(marketIds, newPrices, timestamp);
    }

    function getPrice(bytes32 marketId) external view returns (uint256) {
        uint256 price = prices[marketId];
        require(price > 0, "Price not set for market");
        return price;
    }

    function getPrices(bytes32[] calldata marketIds) external view returns (uint256[] memory) {
        uint256[] memory result = new uint256[](marketIds.length);

        for (uint256 i = 0; i < marketIds.length; i++) {
            uint256 price = prices[marketIds[i]];
            require(price > 0, "Price not set for market");
            result[i] = price;
        }

        return result;
    }

    function getLastUpdated(bytes32 marketId) external view returns (uint256) {
        return lastUpdated[marketId];
    }

    function hasPrice(bytes32 marketId) external view returns (bool) {
        return prices[marketId] > 0;
    }
}

contracts/FxVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

import "./FxPriceOracle.sol";
import "./FxPositionToken.sol";

contract FxVault is Ownable, ReentrancyGuard {
    using ECDSA for bytes32;

    enum Side { Long, Short }

    struct MarketConfig {
        bool isActive;
        bytes32 marketId;
        FxPositionToken longToken;
        FxPositionToken shortToken;
        uint256 maxLeverage;         // 1e18 = 1x, e.g. 1000e18 for 1000x
        uint256 defaultLeverage;     // 1e18 = 1x, e.g. 100e18 for 100x
        uint256 minInitialMarginBps; // in basis points, applied to notional
        uint256 tradeFeeBps;         // simple fee on notional
    }

    struct Position {
        bool exists;
        bytes32 marketId;
        bool isLong;
        uint256 sizeBase;   // 18-decimal base size
        uint256 entryPrice; // 18-decimal USD
        uint256 marginUsd;  // 18-decimal USD
        uint256 leverage;   // 1e18 = 1x
        bytes32 closeAuthHash;
    }

    struct ClosePositionPayload {
        address account;
        bytes32 marketId;
        bool isLong;
        uint256 sizeUsdToClose;
        uint256 maxSlippageBps;
        uint256 deadline;
        uint256 nonce;
    }

    FxPriceOracle public immutable priceOracle;

    // Stable collateral tokens (e.g. CUSD, USDC, USDT) and their decimals
    mapping(address => bool) public isCollateralToken;
    mapping(address => uint8) public collateralDecimals;

    // Collateral balances per user per token
    mapping(address => mapping(address => uint256)) public collateralBalances;

    // MarketId => config
    mapping(bytes32 => MarketConfig) public markets;

    // account => marketId => side => Position
    mapping(address => mapping(bytes32 => mapping(bool => Position))) public positions;

    // Used nonces for pre-signed closes: account => nonce => used
    mapping(address => mapping(uint256 => bool)) public usedCloseNonces;

    uint256 public constant BPS_DENOMINATOR = 10_000;

    event CollateralTokenRegistered(address indexed token, uint8 decimals);
    event CollateralDeposited(address indexed account, address indexed token, uint256 amount);
    event CollateralWithdrawn(address indexed account, address indexed token, uint256 amount);

    event MarketRegistered(bytes32 indexed marketId, address longToken, address shortToken);
    event PositionOpened(address indexed account, bytes32 indexed marketId, bool isLong, uint256 sizeBase, uint256 marginUsd, uint256 leverage);
    event PositionClosed(address indexed account, bytes32 indexed marketId, bool isLong, uint256 sizeBaseClosed, int256 pnlUsd);

    event PreSignedCloseExecuted(address indexed caller, address indexed account, bytes32 indexed marketId, bool isLong, uint256 sizeUsdToClose, uint256 nonce);

    constructor(address _priceOracle) Ownable(msg.sender) {
        require(_priceOracle != address(0), "Invalid oracle address");
        priceOracle = FxPriceOracle(_priceOracle);
    }

    // --- Admin configuration ---

    function registerCollateralToken(address token) external onlyOwner {
        require(token != address(0), "Invalid token");
        require(!isCollateralToken[token], "Already collateral");

        uint8 dec = IERC20Metadata(token).decimals();
        require(dec > 0 && dec <= 18, "Invalid decimals");

        isCollateralToken[token] = true;
        collateralDecimals[token] = dec;

        emit CollateralTokenRegistered(token, dec);
    }

    function registerMarket(
        bytes32 marketId,
        address longToken,
        address shortToken,
        uint256 maxLeverage,
        uint256 defaultLeverage,
        uint256 minInitialMarginBps,
        uint256 tradeFeeBps
    ) external onlyOwner {
        require(marketId != bytes32(0), "Invalid marketId");
        require(!markets[marketId].isActive, "Market exists");
        require(longToken != address(0) && shortToken != address(0), "Invalid tokens");
        require(maxLeverage >= 1e18, "Invalid maxLeverage");
        require(defaultLeverage >= 1e18 && defaultLeverage <= maxLeverage, "Invalid defaultLeverage");

        markets[marketId] = MarketConfig({
            isActive: true,
            marketId: marketId,
            longToken: FxPositionToken(longToken),
            shortToken: FxPositionToken(shortToken),
            maxLeverage: maxLeverage,
            defaultLeverage: defaultLeverage,
            minInitialMarginBps: minInitialMarginBps,
            tradeFeeBps: tradeFeeBps
        });

        emit MarketRegistered(marketId, longToken, shortToken);
    }

    // --- Collateral management ---

    function depositCollateral(address token, uint256 amount) external nonReentrant {
        require(isCollateralToken[token], "Not collateral");
        require(amount > 0, "Amount=0");

        require(IERC20(token).transferFrom(msg.sender, address(this), amount), "Transfer failed");

        collateralBalances[msg.sender][token] += amount;

        emit CollateralDeposited(msg.sender, token, amount);
    }

    function withdrawCollateral(address token, uint256 amount) external nonReentrant {
        require(isCollateralToken[token], "Not collateral");
        require(amount > 0, "Amount=0");

        uint256 bal = collateralBalances[msg.sender][token];
        require(bal >= amount, "Insufficient collateral");

        // NOTE: For v1 we do not enforce maintenance margin checks here; network risk engine is expected
        collateralBalances[msg.sender][token] = bal - amount;

        require(IERC20(token).transfer(msg.sender, amount), "Transfer failed");

        emit CollateralWithdrawn(msg.sender, token, amount);
    }

    // --- Opening positions ---

    function openLong(
        bytes32 marketId,
        address collateralToken,
        uint256 collateralAmount,
        uint256 leverage,
        ClosePositionPayload calldata closePayload,
        bytes calldata closeSignature
    ) external nonReentrant {
        _openPosition(marketId, collateralToken, collateralAmount, leverage, true, closePayload, closeSignature);
    }

    function openShortCashSecured(
        bytes32 marketId,
        address collateralToken,
        uint256 collateralAmount,
        ClosePositionPayload calldata closePayload,
        bytes calldata closeSignature
    ) external nonReentrant {
        // For cash-secured short, leverage is fixed to 1x
        _openPosition(marketId, collateralToken, collateralAmount, 1e18, false, closePayload, closeSignature);
    }

    function _openPosition(
        bytes32 marketId,
        address collateralToken,
        uint256 collateralAmount,
        uint256 leverage,
        bool isLong,
        ClosePositionPayload calldata closePayload,
        bytes calldata closeSignature
    ) internal {
        MarketConfig memory cfg = markets[marketId];
        require(cfg.isActive, "Market inactive");
        require(isCollateralToken[collateralToken], "Not collateral");
        require(collateralAmount > 0, "Collateral=0");
        require(leverage >= 1e18 && leverage <= cfg.maxLeverage, "Invalid leverage");

        // Pull collateral from user into vault
        require(IERC20(collateralToken).transferFrom(msg.sender, address(this), collateralAmount), "Transfer failed");
        collateralBalances[msg.sender][collateralToken] += collateralAmount;

        // Convert collateral to USD 18-dec (we assume stable tokens ~= 1 USD)
        uint8 dec = collateralDecimals[collateralToken];
        uint256 collateralUsd = _toUsd(collateralAmount, dec);

        // Notional in USD
        uint256 notionalUsd = collateralUsd * leverage / 1e18;

        // Margin requirement with possible extra BPS
        if (cfg.minInitialMarginBps > 0) {
            // collateralUsd * 10000 >= notionalUsd * minInitialMarginBps
            require(collateralUsd * BPS_DENOMINATOR >= notionalUsd * cfg.minInitialMarginBps, "Margin too low");
        }

        // Fetch price
        uint256 price = priceOracle.getPrice(marketId);
        require(price > 0, "No price");

        // Base size
        uint256 sizeBase = notionalUsd * 1e18 / price;
        require(sizeBase > 0, "Size=0");

        // Compute fee on notional and lock it as extra margin burn (simple for v1)
        uint256 feeUsd = notionalUsd * cfg.tradeFeeBps / BPS_DENOMINATOR;
        uint256 effectiveMarginUsd = collateralUsd - feeUsd;
        require(effectiveMarginUsd > 0, "Fee>collateral");

        // Update position (single position per (account, marketId, side) in v1)
        Position storage p = positions[msg.sender][marketId][isLong];
        require(!p.exists, "Position exists");

        p.exists = true;
        p.marketId = marketId;
        p.isLong = isLong;
        p.sizeBase = sizeBase;
        p.entryPrice = price;
        p.marginUsd = effectiveMarginUsd;
        p.leverage = leverage;

        // Validate and store closeAuthHash
        _validateAndStoreCloseAuth(msg.sender, marketId, isLong, notionalUsd, closePayload, closeSignature);

        // Mint exposure tokens equal to notional USD
        if (isLong) {
            cfg.longToken.mint(msg.sender, notionalUsd);
        } else {
            cfg.shortToken.mint(msg.sender, notionalUsd);
        }

        emit PositionOpened(msg.sender, marketId, isLong, sizeBase, effectiveMarginUsd, leverage);
    }

    function _validateAndStoreCloseAuth(
        address account,
        bytes32 marketId,
        bool isLong,
        uint256 notionalUsd,
        ClosePositionPayload calldata payload,
        bytes calldata signature
    ) internal {
        require(payload.account == account, "Bad account");
        require(payload.marketId == marketId, "Bad marketId");
        require(payload.isLong == isLong, "Bad side");
        require(payload.sizeUsdToClose <= notionalUsd, "Too large size");
        require(payload.deadline >= block.timestamp, "Close payload expired");
        require(!usedCloseNonces[account][payload.nonce], "Nonce used");

        bytes32 structHash = keccak256(
            abi.encode(
                keccak256(
                    "ClosePositionPayload(address account,bytes32 marketId,bool isLong,uint256 sizeUsdToClose,uint256 maxSlippageBps,uint256 deadline,uint256 nonce)"
                ),
                payload.account,
                payload.marketId,
                payload.isLong,
                payload.sizeUsdToClose,
                payload.maxSlippageBps,
                payload.deadline,
                payload.nonce
            )
        );

        bytes32 digest = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", structHash));
        address signer = digest.recover(signature);
        require(signer == account, "Bad signature");

        Position storage p = positions[account][marketId][isLong];
        p.closeAuthHash = structHash;
    }

    // --- User-initiated closes ---

    function closePosition(
        bytes32 marketId,
        bool isLong,
        uint256 sizeUsdToClose
    ) external nonReentrant {
        _closePositionInternal(msg.sender, marketId, isLong, sizeUsdToClose, false);
    }

    // --- Pre-signed forced closes ---

    function executePreSignedClose(
        ClosePositionPayload calldata payload,
        bytes calldata signature
    ) external nonReentrant {
        require(payload.deadline >= block.timestamp, "Expired");
        require(!usedCloseNonces[payload.account][payload.nonce], "Nonce used");

        bytes32 structHash = keccak256(
            abi.encode(
                keccak256(
                    "ClosePositionPayload(address account,bytes32 marketId,bool isLong,uint256 sizeUsdToClose,uint256 maxSlippageBps,uint256 deadline,uint256 nonce)"
                ),
                payload.account,
                payload.marketId,
                payload.isLong,
                payload.sizeUsdToClose,
                payload.maxSlippageBps,
                payload.deadline,
                payload.nonce
            )
        );

        bytes32 digest = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", structHash));
        address signer = digest.recover(signature);
        require(signer == payload.account, "Bad signature");

        Position storage p = positions[payload.account][payload.marketId][payload.isLong];
        require(p.exists, "No position");
        require(p.closeAuthHash == structHash, "Auth mismatch");

        usedCloseNonces[payload.account][payload.nonce] = true;

        _closePositionInternal(payload.account, payload.marketId, payload.isLong, payload.sizeUsdToClose, true);

        emit PreSignedCloseExecuted(msg.sender, payload.account, payload.marketId, payload.isLong, payload.sizeUsdToClose, payload.nonce);
    }

    function _closePositionInternal(
        address account,
        bytes32 marketId,
        bool isLong,
        uint256 sizeUsdToClose,
        bool isForced
    ) internal {
        require(sizeUsdToClose > 0, "Size=0");

        MarketConfig memory cfg = markets[marketId];
        require(cfg.isActive, "Market inactive");

        Position storage p = positions[account][marketId][isLong];
        require(p.exists, "No position");

        // Determine notional and proportion
        uint256 notionalUsdTotal = p.sizeBase * p.entryPrice / 1e18;
        require(sizeUsdToClose <= notionalUsdTotal, "Too large");

        uint256 proportion = sizeUsdToClose * 1e18 / notionalUsdTotal;
        uint256 sizeBaseToClose = p.sizeBase * proportion / 1e18;

        // Burn exposure tokens
        if (isLong) {
            cfg.longToken.burn(account, sizeUsdToClose);
        } else {
            cfg.shortToken.burn(account, sizeUsdToClose);
        }

        // Get new price
        uint256 price = priceOracle.getPrice(marketId);
        require(price > 0, "No price");

        // PnL computation (signed)
        int256 pnlUsd;
        if (isLong) {
            // long: size * (P_close - P_entry)
            int256 diff = int256(price) - int256(p.entryPrice);
            pnlUsd = int256(sizeBaseToClose) * diff / int256(1e18);
        } else {
            // short: size * (P_entry - P_close)
            int256 diff = int256(p.entryPrice) - int256(price);
            pnlUsd = int256(sizeBaseToClose) * diff / int256(1e18);
        }

        // Margin portion being released
        uint256 marginPortion = p.marginUsd * proportion / 1e18;

        // Update position
        p.sizeBase -= sizeBaseToClose;
        p.marginUsd -= marginPortion;

        if (p.sizeBase == 0) {
            p.exists = false;
        }

        // Simple settlement: assume single stable token backing for now, not tracked per-token here.
        // For MVP, we do not move actual collateral; risk engine can track margin vs PnL off-chain.

        emit PositionClosed(account, marketId, isLong, sizeBaseToClose, pnlUsd);
    }

    // --- Helpers ---

    function _toUsd(uint256 amount, uint8 decimals) internal pure returns (uint256) {
        if (decimals == 18) {
            return amount;
        } else if (decimals < 18) {
            return amount * (10 ** (18 - decimals));
        } else {
            return amount / (10 ** (decimals - 18));
        }
    }
}

contracts/StockPriceOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

contract StockPriceOracle is Ownable {
    uint8 public constant PRICE_DECIMALS = 18;

    mapping(address => uint256) private prices;
    mapping(address => uint256) private lastUpdated;

    event PriceUpdated(address indexed token, uint256 price, uint256 timestamp);
    event PricesBatchUpdated(address[] tokens, uint256[] prices, uint256 timestamp);

    constructor() Ownable(msg.sender) {}

    function updatePrice(address token, uint256 price) external onlyOwner {
        require(token != address(0), "Invalid token address");
        require(price > 0, "Price must be greater than zero");

        prices[token] = price;
        lastUpdated[token] = block.timestamp;

        emit PriceUpdated(token, price, block.timestamp);
    }

    function updatePrices(address[] calldata tokens, uint256[] calldata newPrices) external onlyOwner {
        require(tokens.length == newPrices.length, "Arrays length mismatch");
        require(tokens.length > 0, "Empty arrays");

        uint256 timestamp = block.timestamp;

        for (uint256 i = 0; i < tokens.length; i++) {
            require(tokens[i] != address(0), "Invalid token address");
            require(newPrices[i] > 0, "Price must be greater than zero");

            prices[tokens[i]] = newPrices[i];
            lastUpdated[tokens[i]] = timestamp;
        }

        emit PricesBatchUpdated(tokens, newPrices, timestamp);
    }

    function getPrice(address token) external view returns (uint256) {
        uint256 price = prices[token];
        require(price > 0, "Price not set for token");
        return price;
    }

    function getPrices(address[] calldata tokens) external view returns (uint256[] memory) {
        uint256[] memory result = new uint256[](tokens.length);

        for (uint256 i = 0; i < tokens.length; i++) {
            uint256 price = prices[tokens[i]];
            require(price > 0, "Price not set for token");
            result[i] = price;
        }

        return result;
    }

    function getLastUpdated(address token) external view returns (uint256) {
        return lastUpdated[token];
    }

    function hasPrice(address token) external view returns (bool) {
        return prices[token] > 0;
    }
}

contracts/StockVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IStockPriceOracle {
    function getPrice(address token) external view returns (uint256);
    function PRICE_DECIMALS() external view returns (uint8);
}

interface ITokenizedStock is IERC20 {
    function mint(address to, uint256 amount) external;
    function burn(address from, uint256 amount) external;
}

contract StockVault is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    IStockPriceOracle public immutable priceOracle;

    address public immutable CUSD;
    address public immutable USDC;
    address public immutable USDT;

    struct StableInfo {
        bool isStable;
        uint8 decimals;
    }

    struct StockInfo {
        bool isStock;
        uint8 decimals;
    }

    mapping(address => StableInfo) public stables;
    mapping(address => StockInfo) public stocks;

    event StockRegistered(address indexed stock, uint8 decimals);
    event BuyStock(address indexed user, address indexed stableToken, address indexed stockToken, uint256 stableAmountIn, uint256 stockAmountOut);
    event SellStock(address indexed user, address indexed stableToken, address indexed stockToken, uint256 stockAmountIn, uint256 stableAmountOut);

    constructor(address _priceOracle, address _cusd, address _usdc, address _usdt) Ownable(msg.sender) {
        require(_priceOracle != address(0), "Invalid oracle address");
        require(_cusd != address(0), "Invalid CUSD address");
        require(_usdc != address(0), "Invalid USDC address");
        require(_usdt != address(0), "Invalid USDT address");

        priceOracle = IStockPriceOracle(_priceOracle);
        CUSD = _cusd;
        USDC = _usdc;
        USDT = _usdt;

        _registerStable(_cusd);
        _registerStable(_usdc);
        _registerStable(_usdt);
    }

    function _registerStable(address token) internal {
        uint8 decimals = IERC20Metadata(token).decimals();
        stables[token] = StableInfo({isStable: true, decimals: decimals});
    }

    function registerStock(address stockToken) external onlyOwner {
        require(stockToken != address(0), "Invalid stock address");
        require(!stocks[stockToken].isStock, "Stock already registered");

        uint8 decimals = IERC20Metadata(stockToken).decimals();
        stocks[stockToken] = StockInfo({isStock: true, decimals: decimals});

        emit StockRegistered(stockToken, decimals);
    }

    function quoteBuy(address stableToken, address stockToken, uint256 stableAmount) external view returns (uint256) {
        require(stables[stableToken].isStable, "Unsupported stable");
        require(stocks[stockToken].isStock, "Unsupported stock");
        require(stableAmount > 0, "Amount must be > 0");

        uint256 price = priceOracle.getPrice(stockToken);
        return _quoteBuy(stableToken, stockToken, stableAmount, price);
    }

    function quoteSell(address stockToken, address stableToken, uint256 stockAmount) external view returns (uint256) {
        require(stables[stableToken].isStable, "Unsupported stable");
        require(stocks[stockToken].isStock, "Unsupported stock");
        require(stockAmount > 0, "Amount must be > 0");

        uint256 price = priceOracle.getPrice(stockToken);
        return _quoteSell(stockToken, stableToken, stockAmount, price);
    }

    function buyStock(address stableToken, address stockToken, uint256 stableAmount, uint256 minStockOut) external nonReentrant {
        require(stables[stableToken].isStable, "Unsupported stable");
        require(stocks[stockToken].isStock, "Unsupported stock");
        require(stableAmount > 0, "Amount must be > 0");

        uint256 price = priceOracle.getPrice(stockToken);
        uint256 stockAmountOut = _quoteBuy(stableToken, stockToken, stableAmount, price);
        require(stockAmountOut >= minStockOut, "Slippage");

        IERC20(stableToken).safeTransferFrom(msg.sender, address(this), stableAmount);
        ITokenizedStock(stockToken).mint(msg.sender, stockAmountOut);

        emit BuyStock(msg.sender, stableToken, stockToken, stableAmount, stockAmountOut);
    }

    function sellStock(address stockToken, address stableToken, uint256 stockAmount, uint256 minStableOut) external nonReentrant {
        require(stables[stableToken].isStable, "Unsupported stable");
        require(stocks[stockToken].isStock, "Unsupported stock");
        require(stockAmount > 0, "Amount must be > 0");

        uint256 price = priceOracle.getPrice(stockToken);
        uint256 stableAmountOut = _quoteSell(stockToken, stableToken, stockAmount, price);
        require(stableAmountOut >= minStableOut, "Slippage");

        require(IERC20(stableToken).balanceOf(address(this)) >= stableAmountOut, "Insufficient liquidity");

        IERC20(stockToken).safeTransferFrom(msg.sender, address(this), stockAmount);
        ITokenizedStock(stockToken).burn(address(this), stockAmount);

        IERC20(stableToken).safeTransfer(msg.sender, stableAmountOut);

        emit SellStock(msg.sender, stableToken, stockToken, stockAmount, stableAmountOut);
    }

    function _quoteBuy(address stableToken, address stockToken, uint256 stableAmount, uint256 price) internal view returns (uint256) {
        StableInfo memory sInfo = stables[stableToken];
        StockInfo memory tInfo = stocks[stockToken];
        uint8 priceDecimals = priceOracle.PRICE_DECIMALS();

        uint256 numerator = stableAmount * (10 ** priceDecimals) * (10 ** tInfo.decimals);
        uint256 denominator = price * (10 ** sInfo.decimals);

        return numerator / denominator;
    }

    function _quoteSell(address stockToken, address stableToken, uint256 stockAmount, uint256 price) internal view returns (uint256) {
        StableInfo memory sInfo = stables[stableToken];
        StockInfo memory tInfo = stocks[stockToken];
        uint8 priceDecimals = priceOracle.PRICE_DECIMALS();

        uint256 numerator = stockAmount * price * (10 ** sInfo.decimals);
        uint256 denominator = (10 ** tInfo.decimals) * (10 ** priceDecimals);

        return numerator / denominator;
    }

    function getStableInfo(address token) external view returns (bool isStable, uint8 decimals) {
        StableInfo memory info = stables[token];
        return (info.isStable, info.decimals);
    }

    function getStockInfo(address token) external view returns (bool isStock, uint8 decimals) {
        StockInfo memory info = stocks[token];
        return (info.isStock, info.decimals);
    }

    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        IERC20(token).safeTransfer(owner(), amount);
    }
}

contracts/TokenizedStock.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract TokenizedStock is ERC20, Ownable {
    address public vault;
    bool public bootstrapped;

    event VaultUpdated(address indexed oldVault, address indexed newVault);

    constructor(string memory name_, string memory symbol_, address initialVault) ERC20(name_, symbol_) Ownable(msg.sender) {
        require(initialVault != address(0), "Invalid vault address");
        vault = initialVault;
    }

    modifier onlyVault() {
        require(msg.sender == vault, "Caller is not the vault");
        _;
    }

    function setVault(address newVault) external onlyOwner {
        require(newVault != address(0), "Invalid vault address");
        address oldVault = vault;
        vault = newVault;
        emit VaultUpdated(oldVault, newVault);
    }

    /// @notice One-time mint of exactly 1 token (18 decimals) to a recipient for bootstrap purposes
    /// @dev Can only be called once by the owner; subsequent calls revert
    function bootstrapMint(address to) external onlyOwner {
        require(!bootstrapped, "Already bootstrapped");
        require(to != address(0), "Invalid recipient");
        bootstrapped = true;
        _mint(to, 1e18);
    }

    function mint(address to, uint256 amount) external onlyVault {
        _mint(to, amount);
    }

    function burn(address from, uint256 amount) external onlyVault {
        _burn(from, amount);
    }
}

